import { Component, OnInit } from '@angular/core';
import { CdkDragDrop, moveItemInArray, transferArrayItem } from '@angular/cdk/drag-drop';
import { ColumnsService } from 'src/app/services/columns.service';
import { Column } from 'src/app/models/column.model';

@Component({
  selector: 'app-boards',
  templateUrl: './boards.component.html',
  styleUrls: ['./boards.component.scss']
})
export class BoardsComponent implements OnInit{
  object: Column[] = [];

  columns = ['Test col 1', 'Test col 2', 'Test col 3', 'Test col 4'];
  tasks = ['Test task 1', 'Test task 2', 'Test task 3', 'Test task 4'];


  constructor(private _columnsService: ColumnsService) { }

  ngOnInit(): void {
    this.getColumns();
  }

  getColumns() {
    this._columnsService.getColumns().subscribe(res => this.object = res)
  }
}
