import { Component } from '@angular/core';

@Component({
  selector: 'app-add-task-drawer',
  templateUrl: './add-task-drawer.component.html',
  styleUrls: ['./add-task-drawer.component.scss']
})
export class AddTaskDrawerComponent {

}
<mat-drawer-container class="my-container">
    <mat-drawer #drawer class="my-drawer" mode="over" position="start">
         Drawer Demo
    </mat-drawer>
    <mat-drawer-content>
      <div class="my-drawer-content">
        <button type="button" mat-button (click)="drawer.toggle()">
          Toggle Drawer
        </button>
      </div>
    </mat-drawer-content>
  </mat-drawer-container>
